mapper.update:
UPDATE product
prod_name = #{prodName}, prod_detail = #{prodDetail} , manufacture_day = #{manuDate}, image_file = #{fileName}, price = #{price},
WHERE prod_no = #{prodNo}

mapper.select:
  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(	SELECT p.prod_no, p.prod_name, p.price, p.reg_date, NVL(t.tran_status_code,0)
										FROM product p, transaction t
										WHERE t.prod_no(+) = p.prod_no
										
												
									 				AND p.prod_no = #{searchKeyword}
												
												
									 				AND prod_name = #{searchKeyword}
												
												
									 				AND price = #{searchKeyword}
									 															
										
										ORDER BY prod_no ) inner_table
					WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

	  	SELECT COUNT(*)
	  	FROM(	SELECT prod_no 
						FROM product
						
								
						 			AND p.prod_no = #{searchKeyword}
								
								
						 		AND prod_name = #{searchKeyword}
								
								
						 		AND price = #{searchKeyword}
								

						 ) countTable
